import React,{useState,useEffect} from 'react';
import axios from 'axios';

const AddGame = () => {

    const [title,setTitle] = useState("");
    const [genre,setGenre] = useState("");
    const [played,setPlayed] = useState(false);


    const handleReset = () => {
        setTitle("");
        setGenre("");
        setPlayed(false);
    }

    async function postGames(){
        try{
            const response = await axios({
                url: `http://127.0.0.1:5000/games`, 
                headers: {
                    'Authorization': '',
                    'Content-Type': '',
                },
                data: { title: this.title, genre: this.genre, played: this.played },
                method: 'POST'
                });
                let data = response.data;
                return {
                tokenInfo:data,
                timestamp:new Date().getTime()
                }
                } catch(err) {
                    console.log(err.response)
                }
    }








    return (
            <div>
                <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">
                Add Game
                </button>


                <div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                    <div class="modal-dialog modal-dialog-centered" role="document">
                        <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLongTitle">Add Game</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <div class="form-group row">
                                <label for="inputPassword" class="col-sm-2 col-form-label">Title</label>
                                <div class="col-sm-10">
                                <input onChange={ e => setTitle(e.target.value) } type="text" class="form-control" id="inputPassword" value={title} />
                                </div>
                            </div>

                            <div class="form-group row">
                                <label for="inputPassword" class="col-sm-2 col-form-label">Genre</label>
                                <div class="col-sm-10">
                                <input onChange={ e => setGenre(e.target.value)} type="text" class="form-control" id="inputPassword" value={genre} />
                                </div>
                            </div>

                            <div class="form-check">
                                <input class="form-check-input" onChange={ e => setPlayed(!e.target.checked) } type="checkbox" value={played} id="flexCheckChecked" />
                                <label class="form-check-label" for="flexCheckChecked">
                                    Played
                                </label>
                            </div>    
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" onClick={handleReset} >Reset</button>
                            <button type="button" class="btn btn-success" onClick={postGames} data-dismiss="modal" >Submit</button>
                        </div>
                    </div>
                    </div>
                </div>
            </div>
    );
}

export default AddGame;
