import React, { useState, useEffect } from 'react';
import axios from 'axios';




import './GameTable.css';




//Components
import Game from './Game.js';
import AddGame from './AddGame';
import { response } from 'express';
const GameTable = () => {
    const [data,setData] = useState([]);

    useEffect(() => {
        const dataAxios = async () => {
            const res = await axios('http://127.0.0.1:5000/games');
            setData(res.data.games);
            console.log(data);
        };
        dataAxios();
    },[]);

    const handleDelete = (id) => ( () => {
        axios.delete(`http://localhost:5000/games/${id}`)
        .then(response => console.log(response.response.data))
        .catch(error => console.log(error.response)
        )})



    // DELETE request using axios with error handling
    axios.delete('https://reqres.in/invalid-url')
        .then(response => setStatus('Delete successful'))
        .catch(error => {
            setErrorMessage(error.message);
            console.error('There was an error!', error);
        });






    const listItems = data.map((game) =>
        <Game game={game} handleDelete={handleDelete} />
    );


    return (
        <div className="gameboard">
            <h1>Game Database</h1>
            <AddGame />
            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">Title</th>
                    <th scope="col">Genre</th>
                    <th scope="col">Played ?</th>
                    <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>

                {listItems}

                </tbody>
            </table>

        </div>
    );
};

export default GameTable;
