import axios from 'axios';




import './GameTable.css';




//Components
import Game from './Game.js';

const GameTable = () => {
    const [games,setGames] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/games')
            .then(res => {
                setGames(res.data.games);
                console.log(games)
            })
            .catch(function (error) {
                console.log(error);
            })
    });
















    const game = {title:'God of var',genre:'Actions',played:true};


    return (
        <div className="gameboard">
            <h1>Game Database</h1>
            <button type="button" className="btn btn-info">Add Game</button>
            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">Title</th>
                    <th scope="col">Genre</th>
                    <th scope="col">Played ?</th>
                    <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                    <Game props={game} />
                    <Game props={game} />
                    <Game props={game} />
                    <Game props={game} />
                    <Game props={game} />

                </tbody>
            </table>

        </div>
    );
};

export default GameTable;
